//@ts-ignore-all
import bcrypt from "bcryptjs";
import { client, e } from "app/db.server";


// METHODS
// todo

// START SEEDS
async function seed() {
  const email = "user@tester.com";
  const pass = "talkmate";
  const hash = await bcrypt.hash(pass, 10);

  // const userExisting = e.select(e.User, (u) => ({
  //   filter: e.op(u.email, "=", email),
  // }));

  // cleanup the existing database
  // await e.delete(userExisting).run(client);

  await e.delete(e.User).run(client);
  await e.delete(e.Country).run(client);
  await e.delete(e.Admin).run(client);
  await e.delete(e.AdminProfile).run(client);
  await e.delete(e.Organisation).run(client);
  await e.delete(e.OrganisationProfile).run(client);
  await e.delete(e.OrganisationTeam).run(client);

  // ADMIN
  await e
    .insert(e.AdminPassword, {
      hash,
      admin: e.insert(e.Admin, {
        email: "admin@tester.com"
      })
    })
    .run(client);


  const user = await e.insert(e.User, {
    created_at: new Date(),
    onboarded: false,
    email: email
  });

  await e.set(
    e.insert(e.Password, {
      hash,
      user: user
    }),
    e.insert(e.UserProfile, {
      first_name: "first_name",
      last_name: "last_name",
      user: user
    }),
  ).run(client)



  // ORGANISATION
  // create an organisation with organisation profile
  await e
    .insert(e.Organisation, {
      name: "checkr"
    })
    .run(client);


  // TEMP
  // COUNTRIES
  const countries = [
    { name: "United States" },
    { name: "Canada" },
    { name: "Mexico" }
  ];
  for (const country of countries) {
    await e
      .insert(e.Country, {
        name: country.name
      })
      .run(client);
  }

  //NOTES
  await e
    .set(
      e.insert(e.Note, {
        title: "My first note MODIFIED",
        body: "Hello, world!",
        user
      }),
      e.insert(e.Note, {
        title: "My second note",
        body: "Hello, world!",
        user
      })
    )
    .run(client);


  console.log(`Database has been seeded. ðŸŒ±`);
}

seed()
  .catch((e) => {
    console.error(e);
    process.exit(1);
  })
  .finally(async () => {
    client.close();
  });
